import React, {useState} from "react";
import { Link } from "react-router-dom";

const CartData = ({ dataCart, totalCart}) => {
  const [data, setData] = useState(dataCart);
  const [total, setTotal] = useState(totalCart);
  console.log('data cart:' , dataCart);
  console.log('data nav:' , dataCart);
  handleClick = (e) => {
    const id = e.target.value;
    fetch(`carts/delete/${dataCart[0].cart_id}`);
    // setData([]);
    // setTotal('');
  }

  // console.log(data);
  const allData = data.map((data, index) => (
    <tr key={ index }>
      <td className="nav-text-ins text-nowrap">{dataCart.quantity} x </td>
      <td className="nav-text-ins text-left">{dataCart.product.title}</td>
      <td className="nav-text-ins">${dataCart.product.price * dataCart.quantity}</td>
    </tr>
  ));

  const noData = (
    <tr className="nav-text-ins">
      <td>
        <h4>
          Empty Cart
        </h4>
      </td>
    </tr>
  );

  return (
      <table className="nav-cart">
        <tbody>
          {data.length > 0 ? allData : noData }
          <tr className="total_line">
            <td></td>
            <td className="nav-text-ins text-right r-5">Total </td>
            <td className="nav-text-ins">${ totalCart }</td>
          </tr>
          <tr>
            <td colSpan="2">
              <button className="btn btn-primary" onClick={this.handleClick}
              value={ dataCart }>
                Empty Cart
              </button>
            </td>
          </tr>
        </tbody>
      </table>
  );
};
export default CartData
