import React, {useState, useEffect} from 'react';
import { Link } from "react-router-dom";
import  CartData   from "./CartData"

const NewOrder = ({dataOrder,totalOrder,zeroSmallCart, clearState}) => {
  const [data,setData] = useState(dataOrder);
  const [totalCalc,setTotalCalc] = useState(totalOrder);
  useEffect(()=>{
    setData(dataOrder);
  },[dataOrder]);
  useEffect(()=>{
    setTotalCalc(totalOrder);
  },[totalOrder]);

  const handleChange = () => {
    console.log("change detected");
  }
  const submitForm = () => {
    console.log("Form Submitted");
  }

  return(
    <div className="container mt-5">
      <div className="row">
        <div className="col-sm-12 col-lg-6">
          <h1 className="font-weight-normal mb-5">
            Thank for your order
          </h1>
          <CartData dataCart={data} totalCart={totalCalc} zeroSmallCart={zeroSmallCart} clearState={clearState}/>
          <small id="cartHelp" className="form-text text-muted">
            *Orders dependant on available stock.
          </small>
        </div>
        <div className="col-sm-12 col-lg-6">
          <form onSubmit={submitForm}>
            <div className="form-group">
              <label htmlFor="customerName">Name</label>
              <input
                type="text"
                name="name"
                id="customerName"
                className="form-control"
                required
                onChange={handleChange}
              />
            </div>

            <button type="submit" className="btn custom-button mt-3">
              Create Recipe
            </button>
            <Link to="/products" className="btn btn-link mt-3">
              Back to recipes
            </Link>
          </form>
        </div>
      </div>
    </div>
  )
}

export default NewOrder;
